window.addEventListener('DOMContentLoaded',function(){
  const qualiDate1 = document.getElementById('quali_date_1');
  const qualification1 = document.getElementById('qualification_1');
  qualification1.addEventListener('change', function(){
    if(this.checked) {
      qualiDate1.classList.remove('unvisible');
      qualiDate1.classList.add('visible');
    } else{
      qualiDate1.classList.remove('visible');
      qualiDate1.classList.add('unvisible');
    } 
  });
});

